import { inject, Injectable } from "@angular/core";
import { ActivatedRouteSnapshot, CanActivate, CanActivateChild, CanDeactivate, Resolve, Router, RouterStateSnapshot, UrlTree } from "@angular/router";
import { Observable } from "rxjs";
import { AuthService } from "./auth.service";
import { ContactComponent } from "../contact/contact.component";
import { Course } from "../Models/course";
import { CourseService } from "./course.service";

export interface IDeactiveComponent{
    canExit: () => boolean | Observable<boolean> | Promise<boolean>
}

@Injectable({
    providedIn: 'root'
})
export class AuthGuardService implements CanActivate, CanActivateChild,CanDeactivate<IDeactiveComponent>,Resolve<Course[]> {
    

    authService: AuthService = inject(AuthService);
    router: Router = inject(Router);
    courseService: CourseService = inject(CourseService);

    canActivate(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): 
        boolean | Observable<boolean> | Promise<boolean>
    {
        if(this.authService.IsAuthenticated()){
            return true;
        }else{
            this.router.navigate(['/Login']);
            return false;
        }
    }

    canActivateChild(childRoute: ActivatedRouteSnapshot, state: RouterStateSnapshot)
    {
      return this.canActivate(childRoute,state)  
    }

    canDeactivate(component: IDeactiveComponent, currentRoute: ActivatedRouteSnapshot, currentState: RouterStateSnapshot, nextState: 
                             RouterStateSnapshot)
    {
        return component.canExit();
    }

    resolve(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): Course[] | Observable<Course[]> | Promise<Course[]> {
        return this.courseService.getAllcourses();
    }

}
