import { Component, ElementRef, inject, ViewChild } from '@angular/core';
import { AuthService } from '../Services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
   @ViewChild('username') username:ElementRef;
   @ViewChild('password') password:ElementRef;

   authService: AuthService = inject(AuthService);
   router: Router = inject(Router);

   OnLoginClicked(){
    const username = this.username.nativeElement.value;
    const password = this.password.nativeElement.value;

    const user = this.authService.login(username,password);

    if(user === undefined){
      alert('The login credentials you have entered is incorrect');
    }else{
      alert('Welcome ' + user.name + '. You are logged in.' )
      this.router.navigate(['/Course]')
    }

   }

}
<!-- <div calss="overlay"></div> -->
<div class="login-form-container">
    <div class="login-form">
        <div class="cancel-button-div">
                <i class="fa fa-times" aria-hidden="true"></i>
        </div>
        <h2>Please Login Here</h2>
        <div class="container">
            <label for="uname"><b>Username</b></label>
            <input type="text" placeholder="Enter Username" required #username>

            <label for="psw"><b>Password</b></label>
            <input type="password" placeholder="Enter Password" required #password>

            <button type="submit" (click)="OnLoginClicked()">Login</button>
            <label>
              <input type="checkbox" checked="checked" name="remember"> Remember me
            </label>
          </div>
    </div>
</div>
